import React from 'react';
import { useParams } from 'react-router-dom';
import Carousel from '../components/Carousel';
import data from '../data/logement.json';

function Logement() {
  const { id } = useParams();  // Utilisation de useParams pour récupérer l'id
  const logement = data.find(item => item.id === id);

  if (!logement) {
      return <div>Logement non trouvé</div>;  // Gérer le cas où le logement n'est pas trouvé
  }

  return (
      <div>
          <h1>{logement.title}</h1>
          <Carousel cover={logement.cover} pictures={logement.pictures} />
          {/* Autres informations du logement */}
      </div>
  );
}

export default Logement;
