import React from 'react';
import { useLocation } from 'react-router-dom';
import '../sass/components/banner.scss';

function Banner() {
    const location = useLocation();

    // Déterminer si nous sommes sur la page d'accueil ou sur la page À Propos
    const isHomePage = location.pathname === '/';
    const bannerImage = isHomePage ? '../../utils/image_1.jpg' : '../../utils/image_2.jpg';
    const bannerTitleDisplay = isHomePage ? 'block' : 'none';

    return (
        <div 
            className="banner"
            style={{ backgroundImage: `url(${bannerImage})` }}
        >
            <p 
                className="banner-title"
                style={{ display: bannerTitleDisplay }}
            >
                Chez vous, partout et ailleurs
            </p>
        </div>
    );
}

export default Banner;
