import React from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar'; // Import du composant Navbar
import Footer from '../components/Footer'; // Import du composant Footer
import Carousel from '../components/Carousel';
import Host from '../components/Host';
import Tags from '../components/Tags';
import Rating from '../components/Rating';
import Dropdown from '../components/Dropdown';
import data from '../data/logement.json';

function Logement() {
    const { id } = useParams();
    const logement = data.find((item) => item.id === id);

    if (!logement) {
        return <div>Logement non trouvé</div>;
    }

    return (
        <>
            <Navbar />
            <div className="logement-container">
                <Carousel cover={logement.cover} pictures={logement.pictures} />

                <div>
                    <h1>{logement.title}</h1>
                    <h2>{logement.location}</h2>
                    <Tags tags={logement.tags} />
                </div>
                <div>
                    <Host host={logement.host} />
                    <Rating rating={logement.rating} />
                </div>
                <div>
                    <Dropdown
                        title="Description"
                        items={[logement.description]}
                    />
                    <Dropdown title="Équipements" items={logement.equipments} />
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Logement;
