// export default Rating;
//---------------------------------------
import React from 'react';
import styles from './Rating.module.scss'; // Assure-toi d'importer le fichier SASS

const Rating = ({ rating }) => {
    const renderRatingStars = (rating) => {
        const stars = [];
        for (let i = 0; i < 5; i++) {
            if (i < rating) {
                stars.push(<span key={i} className={styles.on}>★</span>);
            } else {
                stars.push(<span key={i} className={styles.off}>☆</span>);
            }
        }
        return stars;
    };

    return (
        <div className={styles['rating-container']}>
            {renderRatingStars(rating)}
        </div>
    );
};

export default Rating;
