import React, { useState } from 'react';

function Carousel({ cover, pictures }) {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? pictures.length : prevIndex - 1
        );
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === pictures.length ? 0 : prevIndex + 1
        );
    };

    const currentImage =
        currentIndex === 0 ? cover : pictures[currentIndex - 1];

    return (
        <div className="carousel">
            <i
                className="fas fa-chevron-up"
                onClick={handlePrevClick}
                style={{ transform: 'rotate(270deg)' }}
            ></i>
            <img src={currentImage} alt="Logement" className="carousel-image" />
            <i
                className="fa-solid fa-chevron-left"
                onClick={handleNextClick}
                style={{ transform: 'rotate(90deg)' }}
            ></i>
        </div>
    );
}

export default Carousel;
