import React from 'react';
import { useLocation } from 'react-router-dom';
import '../sass/components/banner.scss';

function Banner() {
    const location = useLocation();

    // Déterminer le background image et la visibilité du titre en fonction de la page
    const isHomePage = location.pathname === "/";
    const bannerStyle = {
        backgroundImage: isHomePage ? "url('../utils/image_1.jpg')" : "url('../utils/image_2.jpg')"
    };

    return (
        <div className="banner" style={bannerStyle}>
            {isHomePage && <p className="banner-title">Chez vous, partout et ailleurs</p>}
        </div>
    );
}

export default Banner;
