import React, { useState } from 'react';

function Carousel({ cover, pictures }) {
    const [currentIndex, setCurrentIndex] = useState(0);
    const totalImages = pictures.length + 1; // Inclut l'image de couverture

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === 0 ? totalImages - 1 : prevIndex - 1
        );
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) =>
            prevIndex === totalImages - 1 ? 0 : prevIndex + 1
        );
    };

    const currentImage =
        currentIndex === 0 ? cover : pictures[currentIndex - 1];

    return (
        <div className="carousel">
            <i
                className="fas fa-chevron-up chevron-left"
                onClick={handlePrevClick}
            ></i>
            <img src={currentImage} alt="Logement" className="carousel-image" />
            <i
                className="fas fa-chevron-up chevron-right"
                onClick={handleNextClick}
            ></i>
            <div className="carousel-counter">
                {currentIndex + 1}/{totalImages}
            </div>
        </div>
    );
}

export default Carousel;
